import React, { useEffect, useState } from "react";
import { auth } from "./firebase";
import { useHistory } from "react-router-dom";
import { Card, Button, Image } from "react-bootstrap";
import Header from "../Room/Header";
import "../../App.css";

const ProfilePage = () => {
  let history = useHistory();
  const [cUser, setUser] = useState(auth.currentUser);

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        setUser(user);
      } else {
        history.push("/");
      }
    });
  }, []);

  const handleLogout = async () => {
    if (window.confirm("Are you sure you want to logout?")) {
      try {
        await auth.signOut();
        history.push("/");
      } catch (err) {
        alert(err.message);
      }
    }
  };

  const updatePassword = () => {
    history.push("/changePassword");
  };

  return (
    <>
      <Header />
      <div className="profile-section">
        <h2>Profile</h2>

        <Image
        data-aos="fade-left"
          className="profile-image"
          src="data:image/png;base64,"
          roundedCircle
        />

        <Card data-aos="fade-right" style={{ width: "18rem", color: "white", textAlign: "center", backgroundColor:"#23153c" }}>
          <Card.Body>
            <Card.Title>
              <b>{cUser ? cUser.displayName : "User"}</b>
            </Card.Title>
            <Card.Text>{cUser ? cUser.email : ""}</Card.Text>
            <Button className="auth-button" onClick={updatePassword} variant="dark">
              Change password
            </Button>
            <Button
              style={{ marginLeft: "1rem" }}
              onClick={handleLogout}
              variant="danger"
            >
              Logout
            </Button>
          </Card.Body>
        </Card>
      </div>
    </>
  );
};

export default ProfilePage;
